package be.iswleuven.mediacontroller.cache;

public class CacheException extends Exception {

  /**
   * The serial id.
   */
  private static final long serialVersionUID = 6397061388034076132L;

  /**
   * Create a new cache exception.
   * 
   * @param msg
   */
  public CacheException(String msg) {
    super(msg);
  }
  
}
